import Header from './components/Header'
import Board from './components/Board';
import NameInputer from './components/NameInputer';

import { Grid } from '@mui/material';

import { useEffect, useState } from 'react';
import { API } from 'aws-amplify';
import './App.css';
import { graphqlOperation } from '@aws-amplify/api-graphql';
import { getDrinkers } from './graphQL/queries';

function App() {
  useEffect(() => {
    getDrinkers()
  },[])

  const [drinkers, setDrinkers] = useState([])

  const getGrinkers = () => {
    try{
      const response = API.graphql(
        graphqlOperation(getDrinkers)
      )
      setDrinkers(response.data.getDrinkers.drinkers)
      console.log(response)
    } catch(e){

    }
  }

  return (
    <div className="background-image">
      <div className='header'>
        <Header></Header>
      </div>
      <Grid container>
        <Grid item xs={0.5}>
        </Grid>
        <Grid item xs={4}>
          <Board></Board>
        </Grid>
        <Grid item xs={7.5}>
          <NameInputer></NameInputer>
        </Grid>
      </Grid>
    </div>
  );
}

export default App;
