import coffee from '../images/coffee.png'

import TextField from '@mui/material/TextField';
import { Grid } from '@mui/material';

import React from 'react';

import './components.css'
import { MenuList } from '@mui/material';

function NameInputer() {
    return (
        <div>
            <p className='deadline'>入力は当日12時まで!</p>
            <p className='disclaimer'>我々はSDGs達成のため、フードロス削減を目指します。</p>
            <p className='disclaimer'>コーヒーを飲む方は名前を入力して、コーヒーをタップしてください。</p>
            <Grid container>
                <Grid item xs={8}>
                    <TextField id="outlined-basic" label="おなまえ" required={true} color="success" size='medium' fullWidth  sx={{m:1}}/>
                </Grid>
                <Grid item xs={4}>
                    <div style={{position: 'relative', display:'inline-block'}}>
                        <img src={coffee} style={{ width: '100%', height:'100%'}}></img>
                        <button style={{position:'absolute', top:'50%', left:'50%', transform: 'translete(-50%, -50%)'}}>
                            ボタン
                        </button>
                    </div>

                </Grid>
            </Grid>
        </div>
      );
}

export default NameInputer;
