import Header from './components/Header'
import Board from './components/Board';
import NameInputer from './components/NameInputer';

import { Grid } from '@mui/material';
import { useState } from 'react';

import './App.css';

const App = () => {
  const [renderCount, setRenderCount] = useState(0)
  const [drinkers, setDrinkers] = useState([])

  const rerender = () => {
    setRenderCount(1)
  }

  const drinkersGet = async() => {
    try{
      console.log("drinkersGetをコール")
      const response = await API.graphql(
        graphqlOperation(getDrinkers)
      )
      setDrinkers(response.data.getDrinkers.drinkers)
    } catch(e){
      console.log("クエリ送信に失敗しました")
    }
  }


  return (
    <div className="background-image">
      <div className='header'>
        <Header></Header>
      </div>
      <Grid container>
        <Grid item xs={0.5}>
        </Grid>
        <Grid item xs={4}>
          <Board drinkers={drinkers}></Board>
        </Grid>
        <Grid item xs={7.5}>
          <NameInputer handler={rerender}></NameInputer>
        </Grid>
      </Grid>
    </div>
  );
}

export default App;
